import React, { useEffect, useState } from "react";

const Dashboard = () => {
  const [tasks, setTasks] = useState([]);
  const [filter, setFilter] = useState("All");

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    const res = await fetch("http://localhost:5000/api/tasks");
    const data = await res.json();
    setTasks(data);
  };

  const filteredTasks = tasks.filter((task) => {
    if (filter === "All") return true;
    return task.status === filter;
  });

  return (
    <div>
      <style>{`
        body { background: #f9f9f9; }
        .dashboard-container { max-width: 700px; margin: 40px auto; background: #fff; border-radius: 8px; box-shadow: 0 2px 8px #0001; padding: 32px; }
        h1 { color: #2d3748; }
        .filter-row { margin-bottom: 24px; }
        select { padding: 6px 12px; border-radius: 4px; border: 1px solid #bbb; }
        .task-list { margin-top: 24px; }
        .task-item { background: #f1f5f9; margin-bottom: 12px; padding: 16px; border-radius: 6px; }
        .task-title { font-weight: bold; }
        .task-status { float: right; font-size: 0.95em; color: #555; }
      `}</style>
      <div className="dashboard-container">
        <h1>Task Dashboard</h1>
        <div className="filter-row">
          <label>
            Filter Tasks:{" "}
            <select value={filter} onChange={e => setFilter(e.target.value)}>
              <option value="All">All Tasks</option>
              <option value="Completed">Completed Tasks</option>
              <option value="Ongoing">Ongoing Tasks</option>
              <option value="Pending">Pending Tasks</option>
            </select>
          </label>
        </div>
        <div className="task-list">
          <h2>Task List</h2>
          {filteredTasks.length === 0 ? (
            <div>No tasks found.</div>
          ) : (
            filteredTasks.map(task => (
              <div className="task-item" key={task.id}>
                <span className="task-title">{task.title}</span>
                <span className="task-status">{task.status}</span>
                <div>{task.description}</div>
                <div>
                  <small>
                    Deadline: {task.deadline ? new Date(task.deadline).toLocaleDateString() : "N/A"}
                  </small>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
